<template>
  <div>
    <div class="p-3 my-3" :class="getFontColor(paymentGatewayType)">
      <div class="height-100 d-flex justify-content-center align-items-center">
          <div class="card p-3">
              <div class="d-flex justify-content-between align-items-center"> <img class="paymentGatewayImage" :src="getImageString(paymentGatewayType)"/>
                  <h2>{{bank}}</h2>
              </div>
              <div v-if="paymentGatewayType!=='IdCard'" class="px-2 number mt-3 d-flex justify-content-between align-items-center"> <span>{{cardNumber.substr(0,4)}}</span> <span>{{cardNumber.substr(4,4)}}</span> <span>{{cardNumber.substr(8,4)}}</span> <span>{{cardNumber.substr(12,4)}}</span> </div>
              <div v-if="paymentGatewayType==='IdCard'" class="px-2 number mt-3 d-flex justify-content-between align-items-center"> <span>{{cardNumber}}</span> </div>
              <div class="p-4 card-border mt-4">
                  <div class="d-flex justify-content-between align-items-center"> <span v-if="paymentGatewayType!=='IdCard'" class="cardholder">Card Holder</span> <span v-if="paymentGatewayType==='IdCard'" class="cardholder">Name</span> <span v-if="paymentGatewayType!=='IdCard'" class="expires">Expires</span> </div>
                  <div class="d-flex justify-content-between align-items-center"> <span class="name">xxxx xxxx</span> <span v-if="paymentGatewayType!=='IdCard'" class="date">xx / xx</span> </div>
              </div>
          </div>
      </div>      
  </div>
  </div>
</template>

<script>
export default {
  name: "PaymentCard",
  props: [
    'card-number',
    'bank',
    'card-type',
    'payment-gateway-type',
  ],
  data(){
      return {}
  },
  methods:{
      getImageString(paymentGatewayType){
          const visaImageString = `data:image/png;base64,`;
          const mastercardImageString = `data:image/png;base64,`;
          const maestroImageString = `data:image/png;base64,`;
          const idCardImageString = `data:image/png;base64,`
          switch(paymentGatewayType){
              case 'MasterCard':
                  return mastercardImageString;
              case 'Maestro':
                  return maestroImageString;
              case 'Visa':
                  return visaImageString;
              case 'IdCard':
                return idCardImageString;
          }
      },
      getFontColor(paymentGatewayType){
          if(paymentGatewayType === 'IdCard')
            return 'text-dark';
        return 'text-white';
      }
  }
}
</script>

